public class Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Operators1");
		int a=10, b=7;
		System.out.println("a+b ---> " + (a+b));
		System.out.println("a-b ---> " + (a-b));
		System.out.println("a*b ---> " + (a*b));
		System.out.println("a/b ---> " + (a/b));
		System.out.println("a%b ---> " + (a%b));
	}

}

